import { useEffect, useState } from "react";
import axios from "axios";
import icShipping from "../Utils/ic_shipping.png";
import "../Styles/searchResult.scss";
import { useLocation } from "react-router";

function useQuery() {
    return new URLSearchParams(useLocation().search);
  }

export function SearchResult() {
  const [search, setSearch] = useState("Apple ipod");
  const [searchList, setSearchList] = useState([]);
  const [itemList, setitemList] = useState([]);
  const [categories, setCategories] = useState([])
  const query = useQuery();
  const barra = query.get("#");
  console.log("BARRA", barra);

  const location = useLocation()

  useEffect(() => {
      getData();

  }, []);

  const getData = async () => {
    const products = await axios(`http://localhost:3001/api/items?${search}`);
    setSearchList(products.data);
    setitemList(products.data.items);
    setCategories(products.data.categories.slice(0,5))
    console.log("LCA",location.pathnsearch)
  };
  return (
    <div className="resultContainer">

        <div className="breadcrumb">{categories.map((e,i)=>{
        return <a href="#" key={i}>{e + " > "}</a>})}</div>
      <ul className="productCard">
        {itemList.length
          ? itemList.map((item, i) => {
              return (
                <li key={i} className="Container">
                  <div className="grid-01">
                    <img
                      className="productImg"
                      src={item.picture}
                      alt={item.title}
                    />

                    <div className="grid-02">
                      <div className="grid-03">
                        <a href="#" className="productPrice">{`$${item.price.amount}`}{item.free_shipping?<img className="shippingIco" src={icShipping} alt="icShipping"/>:null}</a>
                        <a href="#" className="productCity">{`${item.address}`}</a>
                      </div>
                      <a href="#" className="productTitle">{item.title}</a>
                    </div>
                  </div>
                </li>
              );
            })
          : null}
      </ul>
    </div>
  );
}
