import "../Styles/ProductDetails.scss";

export function ProductDetails({ details }) {
    console.log(details)
    let condicion
    if(details.condition=="new"){condicion="nuevo"}else{condicion="usado"}
  return (
    <div className="container-details">
      <div className="details01">
        <span>{`${condicion} ${details.sold_quantity}`}</span>
        <span>{details.title}</span>
        {("FFFFFFF", console.log(details))}
      </div>
      <div className="details02">
        <img src={details.picture} alt={details.title} />
      </div>
      <div className="details03">
        <span>{`$ ${details.price.decimals}`}</span>
        <span>{`$ ${details.price.decimals}`}</span>
      </div>
      <div className="details04">
          <button>Comprar</button>
      </div>
    </div>
  );
}
