export function priceFormat(price) {
  return `${price.currency === "ARS" ? "$ " : "U$S "}${price.decimals
    .toString()
    .replace(/\B(?=(\d{3})+(?!\d)\.?)/g, ".")}`;
}

export function decimals(num) {
    return (num.toString()).includes(".")?num.toString().slice(num.length-2):"00" 
}
