import { useEffect, useState } from "react";
import axios from "axios";
import icShipping from "../Utils/ic_shipping.png";
import "../Styles/searchResult.scss";
import { useLocation } from "react-router";
import { Link } from "react-router-dom";
import { Breadcrumb } from "./router"
import {priceFormat, decimalsFormat} from "../Utils/priceFormat.js"


function useQuery() {
    return new URLSearchParams(useLocation().search);
  }

export function SearchResult() {
  //const [search, setSearch] = useState("");
  const [itemList, setitemList] = useState([]);
  const [categories, setCategories] = useState([])

  const query = useQuery();
  const barra = query.get("search");

  useEffect(() => {
      //setSearch(barra)
      getData();
    }, [barra]);

    const getData = async () => {
    const products = await axios(`http://localhost:3001/api/items?${barra}`);
    setitemList(products.data.items);
    setCategories(products.data.categories.slice(0,5))
  };

  return (
<div className="resultContainer">
        <Breadcrumb categories={categories}/>

      <ul className="productCard">
        {itemList.length
          ? itemList.map((item, i) => {
              return (
                <li key={i} className="Container">
                  <div className="grid-01">
                    <Link to={"/items/" + item.id}>
                    <img
                      className="productImg"
                      src={item.picture}
                      alt={item.title}
                    />
                    </Link>

                    <div className="grid-02">
                      <div className="grid-03">
                        <span className="productPrice">{`${priceFormat(item.price)}`}{item.free_shipping?<img className="shippingIco" src={icShipping} alt="icShipping"/>:null}</span>
                        <span className="decimals">{`${decimalsFormat(details.price)}`}</span>
                        <span className="productCity">{`${item.address}`}</span>
                      </div>
                      <span className="productTitle">{item.title}</span>
                    </div>
                  </div>
                </li>
              );
            })
          : null}
      </ul>
    </div>
  );
}
